mapper.update:
UPDATE board
SET ref_step = ref_step + 1
WHERE ref = #{ref}
AND ref_step >= #{ref_step}
And brd_gb = 'C01'

mapper.select:
SELECT *
FROM board b JOIN member m ON(b.id = m.id)
WHERE bno = #{bno}

mapper.update:
UPDATE board
SET title = #{title},
content = #{content}
WHERE bno = #{bno}

mapper.delete:
DELETE FROM board
WHERE brd_gb = 'C01'
AND ref = #{ref}
